config.script:
// constants
var onceMode = "once";
var pulseMode = "pulse";
var triggerMode = "trigger";
var validModes = [onceMode, pulseMode, triggerMode];
var onceActivatedModes = [onceMode, pulseMode];

var content = $.config.getString("content");
var mode = $.config.getString("mode") || onceMode;
var counter = $.config.getInt("counter") || 0;

if (validModes.indexOf(mode) == -1) {
    $.fail("Invalid mode '" + mode + "'");
}

var isOnceActivatedMode = onceActivatedModes.indexOf(mode) != -1;

function emit() {
    $.out(content);
}

function tick(ctx) {
    emit();
}

if (0 === counter) {
    switch (mode) {
        case onceMode:
            $.addGenerator(tick);
            break;
        case pulseMode:
            $.addTimer($.config.getString("duration"), tick);
            break;
    }
}

$.setPortCallback("in", function(ctx, s) {
    counter--;
    if (counter <= 0) {
        if (isOnceActivatedMode) {
            if (counter === 0) {
                switch (mode) {
                    case onceMode:
                        emit();
                        break;
                    case pulseMode:
                        $.addTimer($.config.getString("duration"), tick);
                        break;
                }
            }
        } else {
            emit();
        }
    }
});
